import React from "react";
import Swiper, { Autoplay } from "swiper";
import "swiper/css/autoplay";
import styled from "styled-components";
import slider1 from "../assets/slide1.jpeg";
import slider2 from "../assets/slide2.jpeg";
import slider3 from "../assets/slide3.jpeg";
import slider4 from "../assets/slide4.jpeg";
import slider5 from "../assets/slide5.jpeg";

const Container = styled.div`
  width: 100%;
  height: auto;
  .box {
    width: 100%;
    height: auto;
    object-fit: contain;
  }
`;

const Sliders = () => {
  return (
    <Container>
      <Swiper
        modules={[Autoplay]}
        autoplay={{ delay: 1000 }}
        // Alternative with 3sec default timeout:
        // autoplay={true}
      >
        {/* Add slides here */}
      </Swiper>
    </Container>
  );
};

export default Sliders;
