import React, { useRef } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import { Navigation, EffectFade, Autoplay } from "swiper";
import "swiper/css/navigation";
import "swiper/css/effect-fade";
import "swiper/css/autoplay";
import styled from "styled-components";
import slider1 from "../assets/slide1.jpeg";
import slider2 from "../assets/slide2.jpeg";
import slider3 from "../assets/slide3.jpeg";
import slider4 from "../assets/slide4.jpeg";
import slider5 from "../assets/slide5.jpeg";

const img = { slider1, slider2, slider3, slider4, slider5 };

const Container = styled.div`
  width: 100%;
  height: auto;
  .swiper {
    width: 100%;
    img {
      width: 100%;
      height: auto;
      object-fit: cover;
    }
  }
`;

const Sliders = () => {
  return (
    <Container>
      <Swiper
        modules={[Navigation, EffectFade, Autoplay]}
        navigation
        autoplay={{ delay: 2000 }}
        effect
        speed={800}
        slidesPerView={1}
        loop
        className="swiper"
      >
        <SwiperSlide>
          <img src={slider1} alt="" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slider2} alt="" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slider3} alt="" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slider4} alt="" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slider5} alt="" />
        </SwiperSlide>
      </Swiper>
    </Container>
  );
};

export default Sliders;
